#### Data Collection ####
library(rvest)
library(tidyverse)
url <- "https://baseballsavant.mlb.com/statcast_search?hfPT=&hfAB=&hfGT=R%7C&hfPR=&hfZ=&hfStadium=&hfBBL=&hfNewZones=&hfPull=&hfC=00%7C&hfSea=2025%7C&hfSit=&player_type=batter&hfOuts=0%7C&hfOpponent=&pitcher_throws=&batter_stands=&hfSA=&game_date_gt=2025-03-27&game_date_lt=2025-06-01&hfMo=&hfTeam=&home_road=&hfRO=0%7C&position=&hfInfield=&hfOutfield=&hfInn=&hfBBT=&hfFlag=&metric_1=lineup_cd&metric_1_gt=1&metric_1_lt=1&group_by=team&min_pitches=0&min_results=0&min_pas=0&sort_col=pitches&player_event_sort=api_p_release_speed&sort_order=desc#results"
page <- read_html(url)
mlb <- page %>% html_nodes("table") %>% .[[1]] %>% html_table(fill=T)
mlb <- mlb[,c(3,4)] %>% drop_na()
mlb$lineup_pos <- 1
mlb$exp_runs <- 0.461

scraper <- function(lp, er, url){
  page <- read_html(url)
  mlb2 <- page %>% html_nodes("table") %>% .[[1]] %>% html_table(fill=T)
  mlb2 <- mlb2[,c(3,4)] %>% drop_na()
  mlb2$lineup_pos <- lp
  mlb2$exp_runs <- er
  mlb <<- rbind(mlb, mlb2)
  rm(mlb2)
}

