mlb <- mlb %>% rename(team = Player)
mlb <- mlb %>% rename(ab = Pitches)
#### League Wide ####
library(gt)
library(scales)
library(formattable)
library(gtExtras)
league <- mlb %>% filter(team == "TOTAL")
league <- league %>%
  mutate(ab = gsub(",", "", ab))
league$ab <- as.numeric(league$ab)
league <- league %>% mutate(tot_exp_r = ab * exp_runs)

league_table <- league %>% group_by(lineup_pos) %>%
  summarise(tot_exp_r = sum(tot_exp_r), ab = sum(ab)) %>% mutate(av_exp_r = tot_exp_r/ab)
league_table$av_exp_r <- round(league_table$av_exp_r, digits = 3)
league_table$tot_exp_r <- round(league_table$tot_exp_r, digits = 1)

league_table %>% select(lineup_pos, ab, av_exp_r, tot_exp_r) %>% 
  gt() %>% cols_label(lineup_pos = "Batting Order", ab = "PAs",
                      av_exp_r = html("Run Expectancy per PA<sup>1</sup>"),
                      tot_exp_r = html("Total Run Expectancy<sup>2</sup>")) %>%
  cols_align(align = "center",columns = everything()) %>% 
  tab_options(heading.title.font.size = 32, source_notes.font.size = 12,
              column_labels.font.weight = "bold", column_labels.font.size = 18,
              heading.title.font.weight = "bold", heading.subtitle.font.size = 15,
              footnotes.font.size = 12)  %>% 
  tab_header(title = "Expected Runs by Lineup Position ",
             subtitle = "(MLB March 27 - June 1, 2025)") %>% 
  tab_source_note("Created by Rob Gilligan | @RobG195") %>% 
  tab_style(style = cell_fill(color = "grey92"),
            locations = cells_body(rows = seq(2, 9, 2))) %>%
  tab_style(
    style = cell_fill(color = "gold"),
    locations = cells_body(
      columns = tot_exp_r,
      rows = lineup_pos == "1")) %>% 
  tab_footnote(footnote = html("<sup>1</sup>Rounded to 3 digits")) %>% 
  tab_footnote(
    footnote = html("<sup>2</sup>Sum of PAs multiplied by expected runs for that game situation (RE24 matrix)"))

